MODULE FIELD_API_INTERFACE
  USE PARKIND1, ONLY: JPIM, JPRB
  USE FIELD_MODULE
  IMPLICIT NONE

  TYPE FIELDS_PGP
    CLASS (FIELD_3RB), POINTER :: F_U
    CLASS (FIELD_4RB), POINTER :: F_V
    TYPE (FIELD_3RB_PTR), ALLOCATABLE :: F_PGP_3D(:)
    TYPE (FIELD_2RB_PTR), ALLOCATABLE :: F_PGP_2D(:)  
  END TYPE FIELDS_PGP

   TYPE FIELDS_PSP
       CLASS (FIELD_2RB), POINTER      :: F_PSPVOR
       CLASS (FIELD_2RB), POINTER      :: F_PSPDIV      
       CLASS (FIELD_3RB), ALLOCATABLE  :: F_PSPSC_3D(:)
       CLASS (FIELD_2RB), ALLOCATABLE  :: F_PSPSC_2D(:)
    END TYPE FIELDS_PSP

  TYPE FIELDS_KVSETC
    INTEGER(KIND=JPIM), POINTER :: KVSETUV(:)
    INTEGER(KIND=JPIM), POINTER :: KVSETSC(:)
    INTEGER(KIND=JPIM), POINTER :: KVSETSC_3D(:)
    INTEGER(KIND=JPIM), POINTER :: KVSETSC_2D(:)  
  END TYPE FIELDS_KVSETC

END MODULE FIELD_API_INTERFACE